
create table helo_users (
    user_id serial primary key,
    username varchar,
    password varchar,
    profile_pic varchar
);

create table helo_posts (
    id serial primary key,
    user_id integer,
    foreign key(user_id) references helo_users(user_id)
    title varchar(50),
    image_url varchar,
    content varchar
);

